// import React from "react";
import React, { Component } from "react";
// import loginImg from "../../public/images/login.svg";
import axios from "axios";
// import { connect } from "react-redux";
// import * as actionCreators from "../../../store/creators/actionCreators";
import { setAuthenticationHeader } from "../../utils/Auth";

class LoginContent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {},
    };
  }

  handleLogin = (e) => {
    this.setState({
      user: {
        ...this.state.user,
        [e.target.name]: e.target.value,
      },
    });
  };

  handleLoginPost = () => {
    axios
      .post("http://localhost:3001/api/login", {
        username: this.state.user.username,
        password: this.state.user.password,
      })
      .then((response) => {
        if (response.data.success) {
          const token = response.data.token;
          localStorage.setItem("jsonwebtoken", token);
          setAuthenticationHeader(token);
          console.log(token);
          //   this.props.onAuthenticated(true);
          alert(response.data.message);
          // this.props.history.push("/");
        } else {
          alert(response.data.message);
          alert("response failed");
          this.props.state = {
            user: {
              ...this.state.user,
              password: "",
            },
          };
        }
      });
  };

  handleGuestPost = () => {
    axios.post("http://localhost:3001/api/guest-login", {}).then((response) => {
      if (response.data.success) {
        const token = response.data.token;
        localStorage.setItem("jsonwebtoken", token);
        setAuthenticationHeader(token);
        console.log(token);
        // this.props.onAuthenticated(true);
        alert(response.data.message);
        // this.props.history.push("/");
      } else {
        alert(response.data.message);
        alert("response failed");
        this.props.state = {
          user: {
            ...this.state.user,
            password: "",
          },
        };
      }
    });
  };

  render() {
    return (
      // <h4>Login</h4>
      // <div className="contact-section ptb-100">
      //   <div className="container">
      <div className="row align-items-center">
        <div className="col-lg-6">
          <div className="contact-image">
            <img src="/images/contact.png" alt="image" />
          </div>
        </div>
        <div className="col-lg-6 col-md-6">
          <h2>Login</h2>

          {/* <div className="col-lg-6"> */}
          <div className="contact-form">
            {/* <form> */}
            <div className="row">
              <div className="col-lg-10 col-md-10">
                {/* <div className="form-group"> */}
                <div className="newsletter-form">
                  <input
                    onChange={this.handleLogin}
                    type="text"
                    name="username"
                    placeholder="Username"
                    className="form-control"
                    required
                  />
                </div>
                {/* </div> */}
                <br></br>

                {/* <div className="col-lg-10 col-md-10"> */}
                {/* <div className="form-group"> */}
                <div className="newsletter-form">
                  <input
                    onChange={this.handleLogin}
                    type="password"
                    name="password"
                    placeholder="Password"
                    className="form-control"
                    required
                  />
                </div>
                {/* </div> */}
                <br></br>

                <div className="col-lg-10 col-md-10">
                  <div className="newsletter-form">
                    <button
                      onClick={this.handleLoginPost}
                      type="button"
                      type="submit"
                      className="submit-btn mt-2"
                    >
                      Login
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
          {/* </form> */}
          <br></br>
          {/* <form> */}
          <div className="contact-form">
            <div className="row">
              <div className="col-lg-10 col-md-10">
                {/* <div className="newsletter-form"> */}
                <button
                  onClick={this.handleGuestPost}
                  type="button"
                  type="submit"
                  className="submit-btn mt-2"
                >
                  Guest Login
                </button>
              </div>
            </div>

            {/* </form> */}
          </div>
        </div>
      </div>
      // </div>
      // </div>
    );
  }
}

// const mapDispatchToProps = (dispatch) => {
//   return {
//     onAuthenticated: () => dispatch(actionCreators.authenticated(true)),
//   };
// };

// export default connect(null, mapDispatchToProps)(LoginJSX);

export default LoginContent;

