import React from "react";
// import loginImg from "../../public/images/login.svg";

class RegisterContent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {},
    };
  }

  handleRegister = (e) => {
    this.setState({
      user: {
        ...this.state.user,
        [e.target.name]: e.target.value,
      },
    });
  };

  handleRegisterPost = () => {
    fetch("http://localhost:3001/register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(this.state.user),
    })
      .then((res) => res.json())
      .then((response) => {
        alert(response.message);
        // this.props.history.push("/login");
      });
  };

  render() {
    return (
      <section className="subscribe-section p-0">
        <div className="container">
          <div className="subscribe-content-area mb-0">
            <div className="row align-items-center">
              <div className="col-lg-6 col-md-6">
                <div className="subscribe-image">
                  <img src="/images/subscribe.png" alt="image" />
                </div>
              </div>

              <div className="col-lg-6 col-md-6">
                <div className="subscribe-content">
                  <h2>Register</h2>
                </div>

                <form className="newsletter-form">
                  <input
                    onChange={this.handleRegister}
                    type="text"
                    name="username"
                    placeholder="Username"
                    required
                  />
                  <input
                    onChange={this.handleRegister}
                    type="password"
                    name="password"
                    placeholder="Password"
                    required
                  />
                  <button onClick={this.handleRegisterPost} type="submit">
                    Submit
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default RegisterContent;
